import { useState } from 'react';
import { Pencil, Trash2, Plus, CheckCircle2, XCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Table, TableBody, TableCell, TableHead, TableHeader, TableRow,
} from '@/components/ui/table';
import {
  Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter,
} from '@/components/ui/dialog';
import {
  AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent,
  AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Switch } from '@/components/ui/switch';
import {
  Select, SelectContent, SelectItem, SelectTrigger, SelectValue,
} from '@/components/ui/select';
import type { Anmeldung, Teilnehmer, Kurs } from './types';

interface AnmeldungenTabProps {
  anmeldungen: Anmeldung[];
  teilnehmer: Teilnehmer[];
  kurse: Kurs[];
  onAdd: (a: Omit<Anmeldung, 'record_id'>) => void;
  onUpdate: (id: string, a: Omit<Anmeldung, 'record_id'>) => void;
  onDelete: (id: string) => void;
}

const emptyAnmeldung: Omit<Anmeldung, 'record_id'> = {
  teilnehmer: '', kurs: '', anmeldedatum: new Date().toISOString().split('T')[0], bezahlt: false,
};

export function AnmeldungenTab({ anmeldungen, teilnehmer, kurse, onAdd, onUpdate, onDelete }: AnmeldungenTabProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteId, setDeleteId] = useState<string | null>(null);
  const [editing, setEditing] = useState<Anmeldung | null>(null);
  const [form, setForm] = useState(emptyAnmeldung);

  const openCreate = () => { setEditing(null); setForm({ ...emptyAnmeldung, anmeldedatum: new Date().toISOString().split('T')[0] }); setDialogOpen(true); };
  const openEdit = (a: Anmeldung) => {
    setEditing(a);
    setForm({ teilnehmer: a.teilnehmer, kurs: a.kurs, anmeldedatum: a.anmeldedatum, bezahlt: a.bezahlt });
    setDialogOpen(true);
  };

  const handleSave = () => {
    if (!form.teilnehmer || !form.kurs) return;
    if (editing?.record_id) {
      onUpdate(editing.record_id, form);
    } else {
      onAdd(form);
    }
    setDialogOpen(false);
  };

  const getTeilnehmerName = (id: string) => teilnehmer.find(t => t.record_id === id)?.name || 'â€”';
  const getKursTitel = (id: string) => kurse.find(k => k.record_id === id)?.titel || 'â€”';

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h2 className="text-lg font-semibold text-foreground">Anmeldungen</h2>
        <Button onClick={openCreate} size="sm">
          <Plus className="h-4 w-4 mr-1" /> Neue Anmeldung
        </Button>
      </div>

      <div className="bg-card rounded-xl border border-border overflow-hidden shadow-sm">
        <Table>
          <TableHeader>
            <TableRow className="bg-muted/50">
              <TableHead>Teilnehmer</TableHead>
              <TableHead>Kurs</TableHead>
              <TableHead className="hidden md:table-cell">Anmeldedatum</TableHead>
              <TableHead>Bezahlt</TableHead>
              <TableHead className="w-[80px]"></TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {anmeldungen.length === 0 && (
              <TableRow>
                <TableCell colSpan={5} className="text-center py-8 text-muted-foreground">
                  Noch keine Anmeldungen vorhanden
                </TableCell>
              </TableRow>
            )}
            {anmeldungen.map((a) => (
              <TableRow key={a.record_id} className="hover:bg-accent/50 transition-colors">
                <TableCell className="font-medium">{getTeilnehmerName(a.teilnehmer)}</TableCell>
                <TableCell className="text-muted-foreground">{getKursTitel(a.kurs)}</TableCell>
                <TableCell className="hidden md:table-cell text-muted-foreground text-sm">
                  {a.anmeldedatum && new Date(a.anmeldedatum).toLocaleDateString('de-DE')}
                </TableCell>
                <TableCell>
                  {a.bezahlt ? (
                    <Badge variant="default" className="bg-[hsl(158,60%,42%)] hover:bg-[hsl(158,60%,38%)]">
                      <CheckCircle2 className="h-3 w-3 mr-1" /> Bezahlt
                    </Badge>
                  ) : (
                    <Badge variant="destructive">
                      <XCircle className="h-3 w-3 mr-1" /> Offen
                    </Badge>
                  )}
                </TableCell>
                <TableCell>
                  <div className="flex gap-1">
                    <Button variant="ghost" size="icon-sm" onClick={() => openEdit(a)}>
                      <Pencil className="h-3.5 w-3.5" />
                    </Button>
                    <Button variant="ghost" size="icon-sm" onClick={() => setDeleteId(a.record_id!)}>
                      <Trash2 className="h-3.5 w-3.5 text-destructive" />
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>{editing ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}</DialogTitle>
          </DialogHeader>
          <div className="grid gap-4 py-2">
            <div className="grid gap-2">
              <Label>Teilnehmer *</Label>
              <Select value={form.teilnehmer} onValueChange={v => setForm({ ...form, teilnehmer: v })}>
                <SelectTrigger><SelectValue placeholder="Teilnehmer auswÃ¤hlen..." /></SelectTrigger>
                <SelectContent>
                  {teilnehmer.map(t => (
                    <SelectItem key={t.record_id} value={t.record_id!}>{t.name}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="grid gap-2">
              <Label>Kurs *</Label>
              <Select value={form.kurs} onValueChange={v => setForm({ ...form, kurs: v })}>
                <SelectTrigger><SelectValue placeholder="Kurs auswÃ¤hlen..." /></SelectTrigger>
                <SelectContent>
                  {kurse.map(k => (
                    <SelectItem key={k.record_id} value={k.record_id!}>{k.titel}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="grid gap-2">
              <Label>Anmeldedatum</Label>
              <Input type="date" value={form.anmeldedatum} onChange={e => setForm({ ...form, anmeldedatum: e.target.value })} />
            </div>
            <div className="flex items-center gap-3">
              <Switch checked={form.bezahlt} onCheckedChange={v => setForm({ ...form, bezahlt: v })} />
              <Label>Bezahlt</Label>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setDialogOpen(false)}>Abbrechen</Button>
            <Button onClick={handleSave}>{editing ? 'Speichern' : 'Erstellen'}</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <AlertDialog open={!!deleteId} onOpenChange={() => setDeleteId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Anmeldung lÃ¶schen?</AlertDialogTitle>
            <AlertDialogDescription>
              Diese Anmeldung wird unwiderruflich gelÃ¶scht. Sind Sie sicher?
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Abbrechen</AlertDialogCancel>
            <AlertDialogAction onClick={() => { if (deleteId) { onDelete(deleteId); setDeleteId(null); } }}>
              LÃ¶schen
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

toolUseResult.content:
// Local UI types (will be replaced by LivingApps generated types)
export interface Dozent {
  record_id?: string;
  name: string;
  email: string;
  telefon: string;
  fachgebiet: string;
}

export interface Teilnehmer {
  record_id?: string;
  name: string;
  email: string;
  telefon: string;
  geburtsdatum: string;
}

export interface Raum {
  record_id?: string;
  raumname: string;
  gebaeude: string;
  kapazitaet: number;
}

export interface Kurs {
  record_id?: string;
  titel: string;
  beschreibung: string;
  startdatum: string;
  enddatum: string;
  max_teilnehmer: number;
  preis: number;
  dozent: string; // record_id of Dozent
  raum: string;   // record_id of Raum
}

export interface Anmeldung {
  record_id?: string;
  teilnehmer: string; // record_id of Teilnehmer
  kurs: string;       // record_id of Kurs
  anmeldedatum: string;
  bezahlt: boolean;
}

export type TabType = 'kurse' | 'dozenten' | 'teilnehmer' | 'raeume' | 'anmeldungen';

import { BookOpen, Users, Euro } from 'lucide-react';
import type { Kurs, Anmeldung } from './types';

interface HeroKPIsProps {
  kurse: Kurs[];
  anmeldungen: Anmeldung[];
}

export function HeroKPIs({ kurse, anmeldungen }: HeroKPIsProps) {
  const today = new Date().toISOString().split('T')[0];
  const activeKurse = kurse.filter(k => k.startdatum <= today && k.enddatum >= today).length;
  const totalAnmeldungen = anmeldungen.length;
  const totalRevenue = anmeldungen
    .filter(a => a.bezahlt)
    .reduce((sum, a) => {
      const kurs = kurse.find(k => k.record_id === a.kurs);
      return sum + (kurs?.preis || 0);
    }, 0);

  const kpis = [
    {
      label: 'Aktive Kurse',
      value: activeKurse,
      suffix: `von ${kurse.length}`,
      icon: BookOpen,
      color: 'text-primary',
      bg: 'bg-primary/10',
    },
    {
      label: 'Anmeldungen',
      value: totalAnmeldungen,
      suffix: 'gesamt',
      icon: Users,
      color: 'text-[hsl(158,60%,42%)]',
      bg: 'bg-[hsl(158,60%,42%)]/10',
    },
    {
      label: 'Einnahmen',
      value: totalRevenue.toLocaleString('de-DE', { minimumFractionDigits: 2 }),
      suffix: 'EUR bezahlt',
      icon: Euro,
      color: 'text-[hsl(40,90%,40%)]',
      bg: 'bg-[hsl(40,90%,55%)]/10',
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 md:gap-6">
      {kpis.map((kpi) => (
        <div
          key={kpi.label}
          className="bg-card rounded-xl border border-border p-5 md:p-6 shadow-sm hover:shadow-md transition-shadow"
        >
          <div className="flex items-start justify-between">
            <div className="space-y-1">
              <p className="text-sm font-medium text-muted-foreground tracking-wide uppercase">
                {kpi.label}
              </p>
              <p className="text-3xl font-bold tracking-tight text-foreground">
                {kpi.value}
              </p>
              <p className="text-xs text-muted-foreground">{kpi.suffix}</p>
            </div>
            <div className={`${kpi.bg} ${kpi.color} p-3 rounded-lg`}>
              <kpi.icon className="h-5 w-5" />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

import { useState } from 'react';
import { Pencil, Trash2, Plus } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Table, TableBody, TableCell, TableHead, TableHeader, TableRow,
} from '@/components/ui/table';
import {
  Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter,
} from '@/components/ui/dialog';
import {
  AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent,
  AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import {
  Select, SelectContent, SelectItem, SelectTrigger, SelectValue,
} from '@/components/ui/select';
import type { Kurs, Dozent, Raum } from './types';

interface KurseTabProps {
  kurse: Kurs[];
  dozenten: Dozent[];
  raeume: Raum[];
  onAdd: (kurs: Omit<Kurs, 'record_id'>) => void;
  onUpdate: (id: string, kurs: Omit<Kurs, 'record_id'>) => void;
  onDelete: (id: string) => void;
}

const emptyKurs: Omit<Kurs, 'record_id'> = {
  titel: '', beschreibung: '', startdatum: '', enddatum: '',
  max_teilnehmer: 20, preis: 0, dozent: '', raum: '',
};

export function KurseTab({ kurse, dozenten, raeume, onAdd, onUpdate, onDelete }: KurseTabProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteId, setDeleteId] = useState<string | null>(null);
  const [editing, setEditing] = useState<Kurs | null>(null);
  const [form, setForm] = useState(emptyKurs);

  const openCreate = () => { setEditing(null); setForm(emptyKurs); setDialogOpen(true); };
  const openEdit = (k: Kurs) => {
    setEditing(k);
    setForm({ titel: k.titel, beschreibung: k.beschreibung, startdatum: k.startdatum, enddatum: k.enddatum, max_teilnehmer: k.max_teilnehmer, preis: k.preis, dozent: k.dozent, raum: k.raum });
    setDialogOpen(true);
  };

  const handleSave = () => {
    if (!form.titel || !form.startdatum || !form.enddatum) return;
    if (editing?.record_id) {
      onUpdate(editing.record_id, form);
    } else {
      onAdd(form);
    }
    setDialogOpen(false);
  };

  const getStatus = (k: Kurs) => {
    const today = new Date().toISOString().split('T')[0];
    if (k.startdatum > today) return { label: 'Geplant', variant: 'secondary' as const };
    if (k.enddatum < today) return { label: 'Beendet', variant: 'outline' as const };
    return { label: 'Aktiv', variant: 'default' as const };
  };

  const getDozentName = (id: string) => dozenten.find(d => d.record_id === id)?.name || 'â€”';
  const getRaumName = (id: string) => {
    const r = raeume.find(r => r.record_id === id);
    return r ? `${r.raumname} (${r.gebaeude})` : 'â€”';
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h2 className="text-lg font-semibold text-foreground">Kurse</h2>
        <Button onClick={openCreate} size="sm">
          <Plus className="h-4 w-4 mr-1" /> Neuer Kurs
        </Button>
      </div>

      <div className="bg-card rounded-xl border border-border overflow-hidden shadow-sm">
        <Table>
          <TableHeader>
            <TableRow className="bg-muted/50">
              <TableHead>Titel</TableHead>
              <TableHead className="hidden md:table-cell">Dozent</TableHead>
              <TableHead className="hidden md:table-cell">Raum</TableHead>
              <TableHead className="hidden lg:table-cell">Zeitraum</TableHead>
              <TableHead className="hidden sm:table-cell">Preis</TableHead>
              <TableHead>Status</TableHead>
              <TableHead className="w-[80px]"></TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {kurse.length === 0 && (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                  Noch keine Kurse angelegt
                </TableCell>
              </TableRow>
            )}
            {kurse.map((k) => {
              const status = getStatus(k);
              return (
                <TableRow key={k.record_id} className="hover:bg-accent/50 transition-colors">
                  <TableCell className="font-medium">{k.titel}</TableCell>
                  <TableCell className="hidden md:table-cell text-muted-foreground">{getDozentName(k.dozent)}</TableCell>
                  <TableCell className="hidden md:table-cell text-muted-foreground">{getRaumName(k.raum)}</TableCell>
                  <TableCell className="hidden lg:table-cell text-muted-foreground text-sm">
                    {k.startdatum && new Date(k.startdatum).toLocaleDateString('de-DE')} â€“ {k.enddatum && new Date(k.enddatum).toLocaleDateString('de-DE')}
                  </TableCell>
                  <TableCell className="hidden sm:table-cell font-medium">{k.preis.toLocaleString('de-DE')} â‚¬</TableCell>
                  <TableCell><Badge variant={status.variant}>{status.label}</Badge></TableCell>
                  <TableCell>
                    <div className="flex gap-1">
                      <Button variant="ghost" size="icon-sm" onClick={() => openEdit(k)}>
                        <Pencil className="h-3.5 w-3.5" />
                      </Button>
                      <Button variant="ghost" size="icon-sm" onClick={() => setDeleteId(k.record_id!)}>
                        <Trash2 className="h-3.5 w-3.5 text-destructive" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </div>

      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>{editing ? 'Kurs bearbeiten' : 'Neuer Kurs'}</DialogTitle>
          </DialogHeader>
          <div className="grid gap-4 py-2">
            <div className="grid gap-2">
              <Label>Titel *</Label>
              <Input value={form.titel} onChange={e => setForm({ ...form, titel: e.target.value })} placeholder="z.B. Python Grundkurs" />
            </div>
            <div className="grid gap-2">
              <Label>Beschreibung</Label>
              <Textarea value={form.beschreibung} onChange={e => setForm({ ...form, beschreibung: e.target.value })} placeholder="Kurzbeschreibung..." rows={3} />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label>Startdatum *</Label>
                <Input type="date" value={form.startdatum} onChange={e => setForm({ ...form, startdatum: e.target.value })} />
              </div>
              <div className="grid gap-2">
                <Label>Enddatum *</Label>
                <Input type="date" value={form.enddatum} onChange={e => setForm({ ...form, enddatum: e.target.value })} />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label>Max. Teilnehmer</Label>
                <Input type="number" value={form.max_teilnehmer} onChange={e => setForm({ ...form, max_teilnehmer: Number(e.target.value) })} />
              </div>
              <div className="grid gap-2">
                <Label>Preis (EUR)</Label>
                <Input type="number" step="0.01" value={form.preis} onChange={e => setForm({ ...form, preis: Number(e.target.value) })} />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="grid gap-2">
                <Label>Dozent</Label>
                <Select value={form.dozent} onValueChange={v => setForm({ ...form, dozent: v })}>
                  <SelectTrigger><SelectValue placeholder="AuswÃ¤hlen..." /></SelectTrigger>
                  <SelectContent>
                    {dozenten.map(d => (
                      <SelectItem key={d.record_id} value={d.record_id!}>{d.name}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label>Raum</Label>
                <Select value={form.raum} onValueChange={v => setForm({ ...form, raum: v })}>
