  const [dozenten, setDozenten] = useState<Dozent[]>([]);
  const [teilnehmer, setTeilnehmer] = useState<Teilnehmer[]>([]);
  const [raeume, setRaeume] = useState<Raum[]>([]);
  const [anmeldungen, setAnmeldungen] = useState<Anmeldung[]>([]);

  const loadData = useCallback(async () => {
    try {
      setLoading(true);
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const [k, d, t, r, a] = await Promise.all([
        LivingAppsService.getKurse(),
        LivingAppsService.getDozenten(),
        LivingAppsService.getTeilnehmer(),
        LivingAppsService.getRaeume(),
        LivingAppsService.getAnmeldungen(),
      ]);
      setKurse(k);
      setDozenten(d);
      setTeilnehmer(t);
      setRaeume(r);
      setAnmeldungen(a);
    } catch {
      toast.error('Fehler beim Laden der Daten');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { loadData(); }, [loadData]);

  // CRUD handlers
  const handleAddKurs = async (data: Omit<Kurs, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const created = await LivingAppsService.createKurs(data);
      setKurse(prev => [...prev, created]);
      toast.success('Kurs erstellt');
    } catch { toast.error('Fehler beim Erstellen'); }
  };
  const handleUpdateKurs = async (id: string, data: Omit<Kurs, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.updateKurs(id, data);
      setKurse(await (await import('@/services/livingAppsService')).LivingAppsService.getKurse());
      toast.success('Kurs aktualisiert');
    } catch { toast.error('Fehler beim Aktualisieren'); }
  };
  const handleDeleteKurs = async (id: string) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.deleteKurs(id);
      setKurse(prev => prev.filter(k => k.record_id !== id));
      toast.success('Kurs gelÃ¶scht');
    } catch { toast.error('Fehler beim LÃ¶schen'); }
  };

  const handleAddDozent = async (data: Omit<Dozent, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const created = await LivingAppsService.createDozent(data);
      setDozenten(prev => [...prev, created]);
      toast.success('Dozent erstellt');
    } catch { toast.error('Fehler beim Erstellen'); }
  };
  const handleUpdateDozent = async (id: string, data: Omit<Dozent, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.updateDozent(id, data);
      setDozenten(await (await import('@/services/livingAppsService')).LivingAppsService.getDozenten());
      toast.success('Dozent aktualisiert');
    } catch { toast.error('Fehler beim Aktualisieren'); }
  };
  const handleDeleteDozent = async (id: string) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.deleteDozent(id);
      setDozenten(prev => prev.filter(d => d.record_id !== id));
      toast.success('Dozent gelÃ¶scht');
    } catch { toast.error('Fehler beim LÃ¶schen'); }
  };

  const handleAddTeilnehmer = async (data: Omit<Teilnehmer, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const created = await LivingAppsService.createTeilnehmer(data);
      setTeilnehmer(prev => [...prev, created]);
      toast.success('Teilnehmer erstellt');
    } catch { toast.error('Fehler beim Erstellen'); }
  };
  const handleUpdateTeilnehmer = async (id: string, data: Omit<Teilnehmer, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.updateTeilnehmer(id, data);
      setTeilnehmer(await (await import('@/services/livingAppsService')).LivingAppsService.getTeilnehmer());
      toast.success('Teilnehmer aktualisiert');
    } catch { toast.error('Fehler beim Aktualisieren'); }
  };
  const handleDeleteTeilnehmer = async (id: string) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.deleteTeilnehmer(id);
      setTeilnehmer(prev => prev.filter(t => t.record_id !== id));
      toast.success('Teilnehmer gelÃ¶scht');
    } catch { toast.error('Fehler beim LÃ¶schen'); }
  };

  const handleAddRaum = async (data: Omit<Raum, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const created = await LivingAppsService.createRaum(data);
      setRaeume(prev => [...prev, created]);
      toast.success('Raum erstellt');
    } catch { toast.error('Fehler beim Erstellen'); }
  };
  const handleUpdateRaum = async (id: string, data: Omit<Raum, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.updateRaum(id, data);
      setRaeume(await (await import('@/services/livingAppsService')).LivingAppsService.getRaeume());
      toast.success('Raum aktualisiert');
    } catch { toast.error('Fehler beim Aktualisieren'); }
  };
  const handleDeleteRaum = async (id: string) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.deleteRaum(id);
      setRaeume(prev => prev.filter(r => r.record_id !== id));
      toast.success('Raum gelÃ¶scht');
    } catch { toast.error('Fehler beim LÃ¶schen'); }
  };

  const handleAddAnmeldung = async (data: Omit<Anmeldung, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      const created = await LivingAppsService.createAnmeldung(data);
      setAnmeldungen(prev => [...prev, created]);
      toast.success('Anmeldung erstellt');
    } catch { toast.error('Fehler beim Erstellen'); }
  };
  const handleUpdateAnmeldung = async (id: string, data: Omit<Anmeldung, 'record_id'>) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.updateAnmeldung(id, data);
      setAnmeldungen(await (await import('@/services/livingAppsService')).LivingAppsService.getAnmeldungen());
      toast.success('Anmeldung aktualisiert');
    } catch { toast.error('Fehler beim Aktualisieren'); }
  };
  const handleDeleteAnmeldung = async (id: string) => {
    try {
      const { LivingAppsService } = await import('@/services/livingAppsService');
      await LivingAppsService.deleteAnmeldung(id);
      setAnmeldungen(prev => prev.filter(a => a.record_id !== id));
      toast.success('Anmeldung gelÃ¶scht');
    } catch { toast.error('Fehler beim LÃ¶schen'); }
  };

  return (
    <div className="min-h-screen bg-background">
      <Toaster position="top-right" richColors />

      {/* Header */}
      <header className="border-b border-border bg-card/80 backdrop-blur-sm sticky top-0 z-10">
        <div className="max-w-[1400px] mx-auto px-4 md:px-6 py-4">
          <div className="flex items-center gap-3">
            <div className="bg-primary p-2 rounded-lg">
              <GraduationCap className="h-5 w-5 text-primary-foreground" />
            </div>
            <div>
              <h1 className="text-xl font-bold tracking-tight text-foreground">Kursverwaltung</h1>
              <p className="text-xs text-muted-foreground font-medium">Kurse, Dozenten & Teilnehmer verwalten</p>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-[1400px] mx-auto px-4 md:px-6 py-6 space-y-6">
        {/* Hero KPIs */}
        {loading ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 md:gap-6">
            {[1, 2, 3].map(i => (
              <Skeleton key={i} className="h-[120px] rounded-xl" />
            ))}
          </div>
        ) : (
          <HeroKPIs kurse={kurse} anmeldungen={anmeldungen} />
        )}

        {/* Tab Navigation */}
        <div className="border-b border-border">
          <nav className="flex gap-1 overflow-x-auto pb-px -mb-px" role="tablist">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                role="tab"
                aria-selected={activeTab === tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-2.5 text-sm font-medium whitespace-nowrap border-b-2 transition-colors ${
                  activeTab === tab.id
                    ? 'border-primary text-primary'
                    : 'border-transparent text-muted-foreground hover:text-foreground hover:border-border'
                }`}
              >
                <tab.icon className="h-4 w-4" />
                {tab.label}
              </button>
            ))}
          </nav>
        </div>

        {/* Tab Content */}
        {loading ? (
          <div className="space-y-4">
            <div className="flex justify-between">
              <Skeleton className="h-8 w-32" />
              <Skeleton className="h-8 w-32" />
            </div>
            <Skeleton className="h-[300px] rounded-xl" />
          </div>
        ) : (
          <>
            {activeTab === 'kurse' && (
              <KurseTab
                kurse={kurse}
                dozenten={dozenten}
                raeume={raeume}
                onAdd={handleAddKurs}
                onUpdate={handleUpdateKurs}
                onDelete={handleDeleteKurs}
              />
            )}
            {activeTab === 'dozenten' && (
              <DozentenTab
                dozenten={dozenten}
                onAdd={handleAddDozent}
                onUpdate={handleUpdateDozent}
                onDelete={handleDeleteDozent}
              />
            )}
            {activeTab === 'teilnehmer' && (
              <TeilnehmerTab
                teilnehmer={teilnehmer}
                onAdd={handleAddTeilnehmer}
                onUpdate={handleUpdateTeilnehmer}
                onDelete={handleDeleteTeilnehmer}
              />
            )}
            {activeTab === 'raeume' && (
              <RaeumeTab
                raeume={raeume}
                onAdd={handleAddRaum}
                onUpdate={handleUpdateRaum}
                onDelete={handleDeleteRaum}
              />
            )}
            {activeTab === 'anmeldungen' && (
              <AnmeldungenTab
                anmeldungen={anmeldungen}
                teilnehmer={teilnehmer}
                kurse={kurse}
                onAdd={handleAddAnmeldung}
                onUpdate={handleUpdateAnmeldung}
                onDelete={handleDeleteAnmeldung}
              />
            )}
          </>
        )}
      </main>
    </div>
  );
}

