    return callApi('PATCH', `/apps/${APP_IDS.ANMELDUNGEN}/records/${id}`, { fields });
  }
  static async deleteAnmeldungenEntry(id: string) {
    return callApi('DELETE', `/apps/${APP_IDS.ANMELDUNGEN}/records/${id}`);
  }

}

